package corejava;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Initialization
		int x=20;
		while (x>0) // condition
		{
			System.out.println(x);
			//x-=2;
			x--;
			//x++;
			// you can also use x++
		}
		

	}

}
